--
-- Create procedure `Proc_Employee_Filter`
--
CREATE DEFINER = 'root'@'localhost'
PROCEDURE Proc_Employee_Filter (IN v_Where text, -- keyword truyền vào để tìm kiếm (EmployeeCode | EmployeeName | NULL)
IN v_Offset int, -- số thứ tự của bản ghi bắt đầu lấy
IN v_Limit int -- số bản ghi muốn lấy kể tử Offset
)
COMMENT 'Phân trang và tìm kiếm nhân viên theo mã nhân viên hoặc tên nhân viên
           author: VietDV(10/3/2023)'
BEGIN

  -- Kiểm tra xem keyword đầu vào có trống không
  IF IFNULL(v_Where, '') = '' THEN
  --   Nếu trống thì lấy toàn bộ danh sách nhân viên, sắp xếp theo ngày chỉnh sửa, kể từ nhân viên số v_Offset và lấy v_Limit nhân viên (Phân trang)
  --   Đếm tất cả số bản ghi (Hiển thị số bản ghi)  
  BEGIN
    SELECT
      e.EmployeeId,
      e.EmployeeCode,
      e.FullName,
      e.Gender,
      e.DateOfBirth,
      e.IdentityNumber,
      e.IdentityDate,
      e.IdentityPlace,
      e.Address,
      e.PhoneNumber,
      e.LandLine,
      e.Email,
      e.BankNumber,
      e.BankName,
      e.BankBranch,
      e.DepartmentId,
      e.PositionName,
      e.ModifiedDate,
      e.DepartmentName
    FROM view_employee e
    ORDER BY e.ModifiedDate DESC, e.EmployeeCode DESC
    LIMIT v_Limit
    OFFSET v_Offset;

    SELECT
      COUNT(*)
    FROM employee e;
  END;
  ELSE
  --  Nếu có keyword đầu vào thì lấy toàn bộ danh sách nhân viên có mã hoặc tên chứa keyword, sắp xếp theo ngày chỉnh sửa, kể từ nhân viên số v_Offset và lấy v_Limit nhân viên (Phân trang)
  -- Đếm số bản ghi thoả mãn mã hoặc tên chứa keyword (Hiển thị số bản ghi)  
  BEGIN
    SELECT
      e.EmployeeId,
      e.EmployeeCode,
      e.FullName,
      e.Gender,
      e.DateOfBirth,
      e.IdentityNumber,
      e.IdentityDate,
      e.IdentityPlace,
      e.Address,
      e.PhoneNumber,
      e.LandLine,
      e.Email,
      e.BankNumber,
      e.BankName,
      e.BankBranch,
      e.DepartmentId,
      e.PositionName,
      e.ModifiedDate,
      e.DepartmentName
    FROM view_employee e
    WHERE e.EmployeeCode LIKE CONCAT('%', v_Where, '%')
    OR e.FullName LIKE CONCAT('%', v_Where, '%')
    ORDER BY e.ModifiedDate DESC, e.EmployeeCode DESC
    LIMIT v_Limit
    OFFSET v_Offset;

    SELECT
      COUNT(*)
    FROM employee e
    WHERE e.EmployeeCode LIKE CONCAT('%', v_Where, '%')
    OR e.FullName LIKE CONCAT('%', v_Where, '%');
  END;
  END IF;


END
$$

